  )
values (#{bankCardId,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, #{bankCardPassword,jdbcType=VARCHAR}, 
  #{bankGmtCreate,jdbcType=BIGINT}, #{bankMoney,jdbcType=DECIMAL}, #{bankType,jdbcType=INTEGER}, 
  #{bankCreditLimit,jdbcType=DECIMAL}, #{bankMessage,jdbcType=INTEGER}, #{bankTransactor,jdbcType=VARCHAR}, 
  #{bankCardStatus,jdbcType=INTEGER}, #{bankOverdueMonth,jdbcType=INTEGER}, #{bankOverdueAmount,jdbcType=DECIMAL}
